import { useState,useEffect } from "react";



function App() {
  const [loading, setLoading]=useState(true);
  const [coins, setCoins] = useState([]);
  const [selected,setSelected] =useState("");
  const [coin_name,setCoinName]=useState(""); //coinName만 보여주는 기능
  const [coinprice,setCoinPrice]=useState(0);
  const [dollar,setDollar]=useState(0);
  const [result,setResult]=useState(0);

  const handleDollar=(e)=>{
    setDollar(e.target.value); //입력된 달러 
  } 

  const handleChangeSelect=(e)=>{

    setSelected(e.target.value);

}

  const handleClick=()=>{
    setCoinName(selected);
  }



  useEffect(() => {
    setCoinName(selected); // setSelected 후에 호출되도록 함
  }, [selected]); 

  useEffect(()=>{
    fetch("https://api.coinpaprika.com/v1/tickers")
    .then((response) => response.json())
    .then((json) => 
      {setCoins(json);
      setLoading(false);
      });
    },[]);
  return(

    <div>
      <h1>The Coins! ({coins.length})</h1>
      {loading ? <strong>Loading ...</strong> : null}
      <select onChange={handleChangeSelect} value={selected}>
        {coins.map((coin
        ) => 
        <option key={coin.id} value={coin}>{coin.name} ({coin.symbol}) :
         ${coin.quotes.USD.price }USD</option>)}
      </select>
      <hr/>
        <p>
          선택한 코인 : <b>{coin_name}</b>
        </p>
        <input type="number" value={dollar} onChange={handleDollar} />
        <button onClick={handleClick}>변환</button>
        <div>달러 코인 변환 : {result}</div>
    </div>
  );
}

export default App;
