import { useState,useEffect } from "react";



function App() {
  const [loading, setLoading]=useState(true);
  const [coins, setCoins] = useState([]);
  const [usd,setUsd]=useState('');

  const saveUsd= (event) =>{
    setUsd(event.target.value);
  };

  useEffect(()=>{
    fetch("https://api.coinpaprika.com/v1/tickers")
    .then((response) => response.json())
    .then((json) => 
      {setCoins(json);
      setLoading(false);
      });
    },[]);
  return(

    <div>
      <h1>The Coins! ({coins.length})</h1>
      {loading ? <strong>Loading ...</strong> : null}
      <select>
        {coins.map((coin) => 
        <option>{coin.name} ({coin.symbol}) :
         ${coin.quotes.USD.price }USD</option>)}
      </select>
      <input type="number" placeholder="달러" value={usd} onChange={saveUsd} />
      <button className = {isActive ? "activebtn" : "unactiveBtn"} type="button" 
      id="usdBtn" onClick={saveUsd}>전환</button>
    </div>
  );
}

export default App;
