import { useState,useEffect } from "react";

function Hello(){
  function byFn(){
    console.log("good bye! :)");
  }
  function hiFn(){
    console.log("hello! :)");
    return byFn;
  }

  useEffect(()=>{
    console.log("hello! :)");
    return function(){
      console.log("bye :(")
    }
  },[]);
  useEffect(function(){ console.log("hello! :)");},[]);
  return <h1>Hello</h1>;
}

function App() {

  const [showing, setShowing]=useState(false);
  const onClick=()=>setShowing((prev)=>!prev);
  return(
    <div>
      {showing ? <Hello /> : null}
      <button onClick={onClick}>{showing ? "Hide" : "Show" }</button>
    </div>
  );
}

export default App;
