import { useState,useEffect } from "react";



function App() {
  const [loading, setLoading]=useState(true);
  const [coins, setCoins] = useState([]);
  const [selected,setSelected] =useState("");
  const [dollar,setDollar]=useState(0);
  const [result,setResult]=useState(0);

  const handleDollar=(e)=>{
    setDollar(e.target.value); //입력된 달러 
  } 

  const handleChangeSelect=(e)=>{
    const [name,price]=e.target.value.split(',');
    setSelected(name); //선택된 selected

  }

  const handleClick=(e)=>{
   console.log(selected);

  }




  useEffect(()=>{
    fetch("https://api.coinpaprika.com/v1/tickers")
    .then((response) => response.json())
    .then((json) => 
      {setCoins(json);
      setLoading(false);
      });
    },[]);
  return(

    <div>
      <h1>The Coins! ({coins.length})</h1>
      {loading ? <strong>Loading ...</strong> : null}
      <select onChange={handleChangeSelect} value={selected}>
        {coins.map((coin
        ) => 
        <option value={`${coin.name},${coin.quotes.USD.price}`}>{coin.name} ({coin.symbol}) :
         ${coin.quotes.USD.price }USD</option>)}
      </select>
      <hr/>
        <p>
          선택한 코인 : <b>{selected}</b>
        </p>
        <input type="number" value={dollar} onChange={handleDollar} />
        <button onClick={handleClick}>변환</button>
        <div>달러 코인 변환 : {result}</div>
    </div>
  );
}

export default App;
