import { useState,useEffect } from "react";



function App() {
  const [loading, setLoading]=useState(true);
  const [coins, setCoins] = useState([]);
  const [selected,setSelected] =useState("");
  const [coin_name,setCoinName]=useState(""); //coinName만 보여주는 기능
  const [dollar,setDollar]=useState(0);
  const [result,setResult]=useState(0);
  const [priceinUSD,setPriceInUSD]=useState(0);

  const handleDollar=(e)=>{
    setDollar(e.target.value); //입력된 달러 
    const selectdCoinName=selected;
    const selectdCoin=coins.find(coin=>coin.name===selectdCoinName);
    const priceinUSD=selectdCoin.quotes.USD.price;
    setResult(dollar/priceinUSD);
  } 

  const handleChangeSelect=(e)=>{

    setSelected(e.target.value);

}






  useEffect(()=>{
    fetch("https://api.coinpaprika.com/v1/tickers")
    .then((response) => response.json())
    .then((json) => 
      {setCoins(json);
      setLoading(false);
      });
    },[]);
  return(

    <div>
      <h1>The Coins! ({coins.length})</h1>
      {loading ? <strong>Loading ...</strong> : null}
      <select onChange={handleChangeSelect} value={selected}>
        {coins.map((coin) => 
        <option key={coin.id} value={coin.name}>{coin.name} ({coin.symbol}) :
         ${coin.quotes.USD.price }USD</option>)}
      </select>
      <hr/>

    </div>
  );
}

export default App;
